INSERT INTO `perf_performance_content` VALUES (236, 1017, 186, '月活跃用户环比增长达到15%以上（4分标准）', 'MAU从85万增长至98万，环比增长15.3%，获得4分', 10.00, '用户增长与活跃度同步提升，通过精细化运营和用户分层策略，实现了健康增长。新增用户质量较高，7日留存表现良好。', 0.00, NULL, 0, '0', '110', '2025-12-10 17:19:25', '110', '2025-12-10 17:44:02');
