INSERT INTO `perf_performance_content` VALUES (239, 1017, 189, '30日留存率达到30%-40%（4分标准）', '30日留存率从28%提升至33%，获得4分', 8.00, '新用户引导流程优化效果显著，通过个性化内容和精准推送，提升了用户生命周期价值。7日留存率同步提升至45%。', 0.00, NULL, 0, '0', '110', '2025-12-10 17:19:25', '110', '2025-12-10 17:44:02');
INSERT INTO `perf_performance_content` VALUES (240, 1017, 190, '用户平均单次使用时长增长10%-20%（4分标准）', '人均使用时长相较上季度增长18.5%，获得5分', 8.00, '内容质量提升和用户体验优化取得成效，特别是视频内容和社区互动的时长增长明显。建立了内容质量评估体系。', 0.00, NULL, 0, '0', '110', '2025-12-10 17:19:25', '110', '2025-12-10 17:44:02');
