INSERT INTO `perf_performance_content` VALUES (243, 1017, 193, 'ARPU增长8%-15%（4分标准）', ' ARPU从156元提升至178元，增长14.1%，获得4分', 8.00, '通过交叉销售和向上销售策略，提升了用户付费深度。推出了会员体系和增值服务包，用户付费意愿增强。', 0.00, NULL, 0, '0', '110', '2025-12-10 17:19:25', '110', '2025-12-10 17:44:02');
INSERT INTO `perf_performance_content` VALUES (244, 1017, 194, ' 完成运营活动营收目标100%-120%（4分标准）', '运营活动带来营收850万元，完成目标113%，获得4分', 8.00, '成功策划了季度大促和专题活动，通过精准营销和用户分层触达，实现了营收目标。活动转化效率持续提升。', 0.00, NULL, 0, '0', '110', '2025-12-10 17:19:25', '110', '2025-12-10 17:44:02');
